INSERT INTO `tbl_food` VALUES (2,'Chào thu với món lẩu cá khoai thơm ngon tuyệt vời','Thịt cá khoai chín mềm, trong suốt, cảm nhận được miếng cá khoai mềm tan trên đầu lưỡi khi ăn. Vị ngọt thanh đặc trưng của cá khoai và mực tươi quyện cùng chút chua dịu, cay cay của nước lẩu thực sự tạo hấp dẫn bạn. Cùng xem hướng dẫn làm lẩu cá khoai dưới đây nhé!','http://sotaynauan.com/wp-content/uploads/2015/09/chao-thu-voi-mon-lau-ca-khoai-thom-ngon-tuyet-voi-buoc-2.jpg',0,'Cá khoai;Mực tươi;Xương ống heo;Cà chua;Hành lá;Thì là;Rau nhúng lẩu;Hành củ, me chua, nước mắm, ớt, bột nêm, muối, dầu ăn;',2),(3,'Hướng dẫn món sườn chiên tỏi tẩm bột giòn ngon ngất ngây','Món sườn chiên tỏi tẩm bột này sẽ làm cả gia đình bạn thích thú với sự giòn rụm của vỏ ngoài được tẩm bột chiên và vị dai dai của sườn, thật là hấp dẫn.','http://sotaynauan.com/wp-content/uploads/2015/08/huong-dan-mon-suon-chien-toi-tam-bot-gion-ngon-ngat-ngay-ket-qua-1.jpg',0,'Sườn;Tỏi;30ml xì dầu; 15ml rượu nấu ăn; 2.5g đường; 2.5g muối; 30g bột bắp; 30g bột chiên xù; 10g gừng; 20g hành lá;',2),(4,'Gỏi bò cà pháo ăn là ghiền','Gỏi bò cà pháo là món ăn tuy đơn giản nhưng lại rất hấp dẫn và kích thích vị giác. Với hương vị đặc trưng của bò, kết hợp với cái giòn giòn của cà pháo, hương thơm đặc trưng của các loại rau làm cho món ăn thêm phần hấp dẫn.Cùng học cách làm món ăn hấp dẫn này cùng Sổ tay nấu ăn nhé!','http://sotaynauan.com/wp-content/uploads/2015/08/50136.jpg',0,'Thị bò mềm;Cà pháo muối;Sả, quất, ớt, tỏi, dứa;Rau sống các loại (tía tô, diếp cá,...);Gia vị;',8),(5,'Cách làm gà hầm sả quyến rũ cả nhà','Chúng ta đều biết gà và sả là những thực phẩm tốt cho sức khoẻ, chính vì thế sotaynauan.com xin giới thiệu đến các bạn cách làm gà hầm sả tuyệt ngon này.','http://sotaynauan.com/wp-content/uploads/2015/08/cach-lam-ga-ham-sa-quyen-ru-ca-nha-ket-qua.jpg',0,'Gà đùi;100g gừng 1 nửa đập dập, 1 nửa thái chỉ;Tỏi;Sả 4 củ, 2 củ thái lát mỏng, 2 củ để nguyên đập dập;Hành lá;',2),(6,'Hướng dẫn cho món gà om nguyên kiểu siêu hấp dẫn','Cùng vào bếp làm món gà om nguyên con chiêu đãi cả nhà nào, cực kì hấp dẫn mùi vị thì tuyệt ngon luôn đó!','http://sotaynauan.com/wp-content/uploads/2015/08/huong-dan-cho-mon-ga-om-nguyen-kieu-sieu-hap-dan-ket-qua.jpg',0,'Gà đã được làm sạch;Muối vừa đủ để rửa gà;Nước sốt;',2),(7,'Cách làm ốc xào sả ớt cực thơm và cực giòn','Món ốc xào sả ớt có vị cay cay của ớt, vị giòn dai của ốc xào và vị thơm của sả sẽ tạo nên một món ăn ngon tuyệt đó.','http://sotaynauan.com/wp-content/uploads/2015/08/cach-lam-oc-xao-sa-ot-cuc-thom-va-cuc-gion-ket-qua.jpg',0,'Ốc (ốc bươu, ốc mít, ốc vặn...);Sả 7 cây, hành lá vừa đủ;Ớt;Vài tép tỏi;',2),(8,'Cách làm đậu phụ sốt vừng xiên que cực thơm ngon','Bạn đã thử xiên đậu phụ vào những chiếc que nhìn giống thịt nướng chưa, hãy cùng mua nguyên liệu và bắt tay làm ngay món đậu phụ sốt vừng mới lạ này nhé!','http://sotaynauan.com/wp-content/uploads/2015/08/cach-lam-dau-phu-sot-vung-xien-que-cuc-thom-ngon-ket-qua.jpg',0,'Đậu phụ;20ml sốt tỏi, 150gr đậu phụ non Nhật Bản;50gr lạc rang giã nhỏ, 15gr mè trắng, 15gr bột mè đen;Ớt bột, que tre;',2),(9,'Hướng dẫn làm đậu phụ bọc thịt gà chiên giòn rụm','Món đậu phụ bọc thịt gà chiên giòn chỉ nhìn thấy cũng muốn ăn luôn rùi,nguyên liệu và cách  làm thì lại cực dễ,còn chần chừ gì mà không làm thử món này nhỉ?','http://sotaynauan.com/wp-content/uploads/2015/08/huong-dan-lam-dau-phu-boc-thit-ga-chien-gion-rum-tuyet-ngon-ket-qua.jpg',0,'Thịt ức gà (băm hoặc xay nhỏ);Bìa đậu phụ trắng;Đậu phụ rán;1/3 quả ớt chuông xanh, 1/3 quả ớt chuông đỏ;Hành tây;Bột ngô;',2),(10,'Hướng dẫn làm món nghêu nướng ốc luộc đơn thơm ngon hấp dẫn','Nghêu nướng ốc luộc là những món đơn giản, nguyên liệu dễ kiếm mà hương vị lại rất thơm ngon, bổ dưỡng rất thích hợp cho những ngày cuối tuần rảnh rỗi thưởng thức cùng bạn bè hoặc đãi ông xã nhâm nhi đấy. Sau đây sổ tay nấu ăn xin hướng dẫn bạn làm món nghêu nướng ốc luộc đơn giản thơm ngon cho ông xã nhâm nhi cuối tuần! Cùng theo dõi nhé các bạn!','http://sotaynauan.com/wp-content/uploads/2015/07/huong-dan-lam-mon-ngheu-nuong-oc-luoc-don-gian-thom-ngon-cho-ong-xa-nham-nhi-cuoi-tuan-6.jpg',0,'Nghêu, bơ, tỏi bằm, tiêu.;Ốc, vài lá chanh;Nước mắm sả ớt ăn kèm;',2),(11,'Hướng dẫn làm đậu phụ kẹp phô mai chiên xù ngon mê mẩn','Đậu phụ kẹp phô mai chiên xù là sự kết hợp giữa Âu – Á vô cùng hài hoà, ngon, đẹp mắt, bạn sẽ chỉ muốn ăn mãi không thôi.','http://sotaynauan.com/wp-content/uploads/2015/08/huong-dan-lam-dau-phu-kep-pho-mai-chien-xu-ngon-me-man-ket-qua.jpg',0,'Bìa đậu phụ cỡ vừa (tùy lượng ăn);Thịt xông khói;Phô mai;Trứng gà;Muối, bột mì, bột chiên xù;',2),(12,'Cách làm gỏi xoài mực khô lạ miệng mà ngon','Gỏi xoài mực khô sẽ khiến bạn thích chỉ sau lần đầu tiên nếm thử bởi vị thơm đặc trưng của mực nướng, vị thơm của húng quế và một ít chua chua của xoài. ','http://sotaynauan.com/wp-content/uploads/2015/08/cach-lam-goi-xoai-muc-kho-la-mieng-ma-ngon-ket-qua.jpg',0,'Xoài xanh;Mực khô;Rau húng thơm, hành khô, quả ớt tươi.;Lạc rang chín;Chanh;Gia vị;',2),(13,'Cách làm bò cuốn lá cải ngon đến mê mẩn','Món ăn thanh mát lại dễ làm bởi nguyên liệu đơn giản và hướng dẫn chi tiết dưới đây chắc chắn sẽ khiến cả nhà bạn mê mẩn món bò cuốn lá cải này.','http://sotaynauan.com/wp-content/uploads/2015/08/11721247_515332165285296_666919990_n.jpg',0,'Thịt bò thăn;Lá rau cải xanh;Đồ cuốn;Bánh đa nem loại cuốn ăn sống.;',2),(14,'Cách làm gỏi lưỡi heo đu đủ cực giòn và ngon','Thay vì những món xào, nấu, chiên rán thông thường thay đổi thực đơn bằng món gỏi lưỡi heo đu đủ không phải ý tưởng tồi chút nào.','http://sotaynauan.com/wp-content/uploads/2015/08/11720487_515331208618725_761730508_n.jpg',0,'Lưỡi lợn;Đu đủ;Cà rốt: 1 củ; Tỏi: 1 củ; Ớt: 1 quả; Chanh: 1-2 quả;Rau thơm các loại;Gia vị;',2),(15,'Cách làm bún măng vịt thơm ngon đúng vị','Món bún măng vịt không hề xa lạ, là món ăn quen thuộc nhưng cách chế biến sao cho món ăn thơm ngon chuẩn hương vị thì không phải ai cũng biết.','http://sotaynauan.com/wp-content/uploads/2015/08/11715978_515919121893267_1972876661_n.jpg',0,'Vịt cỏ;Măng tươi;Hành hoa;Gừng;Ớt;Hành khô;Rau sống ăn kèm;Gia vị;Bún ăn kèm;',2),(16,'Công thức cho món tôm luộc quế, hoa hồi ngon mê ly','Tôm chứa nhiều canxi rất tốt cho cơ thể, thay vì món tôm luộc thông thường sẽ ngon hơn khi thêm chút hoa hồi, quế, hạt tiêu. Cùng xem bí quyết cho món tôm luộc này nhé!','http://sotaynauan.com/wp-content/uploads/2015/08/11694285_515333548618491_679354868_n.jpg',0,'Tôm tươi;Nước luộc;',2),(17,'Cách làm mì vịt tiềm thơm ngon đúng điệu','Một món ăn đầy dinh dưỡng sẽ giúp cả nhà bổ sung năng lượng để học tập, làm việc và vui chơi, cùng xem cách làm mì vịt tiềm nhé!','http://sotaynauan.com/wp-content/uploads/2015/08/11733434_517125178439328_1955787508_n.jpg',0,'Thịt vịt ( đùi hoặc ức_;Xương heo;Nấm đông cô;Thảo mộc (hoa hồi, quế, đinh hương, cam thảo);Cải thìa;Gia vị;Gừng, rượu trắng, dầu ăn;Mì trứng;',2),(18,'Hướng dẫn làm mì xào thập cẩm ngon hết nức','Mì xào thập cẩm là món ăn dễ làm , dễ ăn, màu sắc bắt mắt, hương vị thì khỏi chê, chị em cùng tham khảo cách làm dưới đây nhé!','http://sotaynauan.com/wp-content/uploads/2015/08/11715870_516068388545007_1709386421_n.jpg',0,'Mì ăn liền dùng để xào;15ml xì dầu; 5ml xì dầu loại đậm màu; 5ml dầu hào; 2.5g đường; 2.5ml dầu mè; 1 ít hạt tiêu trắng; 30ml dầu hạt cải; 2 tép tỏi thái lát; ¼ chén ớt chuông đỏ thái nhỏ; 5 cái nấm hương tươi; thái lát; 1 củ cà rốt bào sợi; 1 bát bắp cải thái sợi nhỏ; 1 chén đậu Hà Lan, cắt chéo; 1 chén giá đỗ; 2 nhánh hành lá; 15 ml rượu nấu ăn;',2),(19,'Hướng dẫn làm miến nấu thịt viên siêu hấp dẫn','Cuối tuần chiêu đãi cả nhà miến nấu thịt viên đầy hấp dẫn này nhé!','http://sotaynauan.com/wp-content/uploads/2015/08/11668094_516068155211697_2087323217_n.jpg',0,'Đậu phụ cắt lát;Thịt vai băm nhỏ;30g hạt dẻ; 5g gừng băm; 2 nhánh hành lá, phần màu trắng băm nhỏ, phần màu xanh xắt nhỏ; 1 lỏng đỏ trứng; 2.5g đường; 10ml rượu nấu ăn; 2.5g muối; 3 muỗng cà phê tỏi băm; 1 nhúm hạt tiêu; 3 muỗng cà phê xì dầu; 2 muỗng cà phê bột bắp; 4 muỗng canh dầu hạt cải hoặc dầu thực vật; 300ml nước luộc gà; lá cải thảo, thái nhỏ; 2 gói bún khô; 5 cây cải chíp chẻ nhỏ;',2),(20,'Hướng dẫn làm thịt bò xào ớt ngon đến miếng cuối cùng','Món thịt bò xào ớt nóng hổi rất thích hợp trong thời tiết mưa gió, vừa nhậu mà ăn cùng cơm nóng cũng rất tuyệt.','http://sotaynauan.com/wp-content/uploads/2015/08/11720513_517130078438838_1262457261_n.jpg',0,'Thịt bò, loại ngon, cắt thành các dải mỏng;Baking soda;10ml xì dầu; 2.5ml dầu mè; 5ml bột ngô; 5ml dầu ăn;30ml dầu ăn; 3 tép tỏi, thái lát; 8 quả ớt loại để xào, dài, thái thành các dải dài nhỏ; 1 1uar ớt đỏ (loại để xào), cùng thái dải dài.;15ml rượu; 2.5g muối; 5ml xì dầu; tiêu trắng;',2),(21,'Cách làm món mực xốt chua cay cho bữa cơm thêm ngon miệng','Mực xốt chua cay là một món ăn ngon và bổ dưỡng nhưng không phải chị em phụ nữ nào cũng biết chế biến cho đúng cách. Mực ống xào không chỉ đậm đà thấm vị mà còn phải giòn dai như vậy mới đạt yêu cầu. Vậy mọi người hãy tham khảo cách làm dưới đây nhé, rất đơn giản mà lại hiệu quả lắm đó.','',0,'',2),(22,'Cách làm nộm đu đủ ngon tại nhà',' Nộm đu đủ không chỉ là món ăn trong bữa cơm thường ngày của nhiều gia đình mà còn là đồ ăn vặt của nhiều bạn teen. Hãy cùng sotaynauan.com học cách làm món ngon này nhé!','http://sotaynauan.com/wp-content/uploads/2015/05/cach-lam-nom-du-du.jpg',0,'Đu đủ xanh;Cà rốt;Lạc rang;bột canh, nước mắm, mì chính...;',2),(23,'Cách làm salad cá ngừ ngon như ngoài tiệm','Salad cá ngừ – món ngon của người sành ăn, bạn hoàn toàn có thể tự tay làm để đãi cả nhà, không chỉ ngon mà con rất tốt cho sức khoẻ nữa.','http://sotaynauan.com/wp-content/uploads/2015/06/cach-lam-salad-ca-ngu-ngon-nhu-ngoai-tiem-ket-qua..jpg',0,'Phi lê cá ngừ;Rau xà lách;Rau cải mầm;Củ hành tây;Gia vị;Thành phần nước sốt;',2),(24,'Hướng dẫn làm mực nhồi thịt nướng thơm nức mũi','Mực nhồi thịt nướng cực kì thơm ngon và hấp dẫn, cuối tuần cùng cả nhà làm món này thì hết ý.','http://sotaynauan.com/wp-content/uploads/2015/06/11301458_497698867048626_1934992